'use client'

import { useState, useEffect, createContext, useContext, ReactNode } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { X, CheckCircle, AlertCircle, Info, AlertTriangle } from 'lucide-react'

interface Toast {
  id: string
  type: 'success' | 'error' | 'warning' | 'info'
  title: string
  description?: string
  duration?: number
}

interface ToastContextType {
  addToast: (toast: Omit<Toast, 'id'>) => void
  removeToast: (id: string) => void
}

const ToastContext = createContext<ToastContextType | undefined>(undefined)

export const useToast = () => {
  const context = useContext(ToastContext)
  if (!context) {
    throw new Error('useToast must be used within a ToastProvider')
  }
  return context
}

export const ToastProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [toasts, setToasts] = useState<Toast[]>([])

  const addToast = (toast: Omit<Toast, 'id'>) => {
    const id = Math.random().toString(36).substr(2, 9)
    const newToast: Toast = {
      ...toast,
      id,
      duration: toast.duration || 5000,
    }

    setToasts(prev => [...prev, newToast])

    // Auto remove toast after duration
    setTimeout(() => {
      removeToast(id)
    }, newToast.duration)
  }

  const removeToast = (id: string) => {
    setToasts(prev => prev.filter(toast => toast.id !== id))
  }

  return (
    <ToastContext.Provider value={{ addToast, removeToast }}>
      {children}
      <Toaster toasts={toasts} removeToast={removeToast} />
    </ToastContext.Provider>
  )
}

interface ToasterProps {
  toasts: Toast[]
  removeToast: (id: string) => void
}

const Toaster: React.FC<ToasterProps> = ({ toasts, removeToast }) => {
  const getIcon = (type: Toast['type']) => {
    switch (type) {
      case 'success':
        return <CheckCircle className="w-5 h-5 text-green-500" />
      case 'error':
        return <AlertCircle className="w-5 h-5 text-red-500" />
      case 'warning':
        return <AlertTriangle className="w-5 h-5 text-yellow-500" />
      case 'info':
        return <Info className="w-5 h-5 text-blue-500" />
    }
  }

  const getBorderColor = (type: Toast['type']) => {
    switch (type) {
      case 'success':
        return 'border-green-500'
      case 'error':
        return 'border-red-500'
      case 'warning':
        return 'border-yellow-500'
      case 'info':
        return 'border-blue-500'
    }
  }

  return (
    <div className="fixed top-4 right-4 z-50 space-y-2">
      <AnimatePresence>
        {toasts.map((toast) => (
          <motion.div
            key={toast.id}
            initial={{ opacity: 0, x: 100, scale: 0.8 }}
            animate={{ opacity: 1, x: 0, scale: 1 }}
            exit={{ opacity: 0, x: 100, scale: 0.8 }}
            transition={{ type: 'spring', stiffness: 300, damping: 30 }}
            className={`bg-terminal-hover border-2 ${getBorderColor(toast.type)} rounded-lg p-4 shadow-lg backdrop-blur-sm max-w-sm`}
          >
            <div className="flex items-start gap-3">
              {getIcon(toast.type)}
              <div className="flex-1 min-w-0">
                <div className="text-terminal-text font-medium text-sm">
                  {toast.title}
                </div>
                {toast.description && (
                  <div className="text-terminal-secondary-text text-xs mt-1">
                    {toast.description}
                  </div>
                )}
              </div>
              <button
                onClick={() => removeToast(toast.id)}
                className="text-terminal-secondary-text hover:text-terminal-text transition-colors"
              >
                <X className="w-4 h-4" />
              </button>
            </div>
          </motion.div>
        ))}
      </AnimatePresence>
    </div>
  )
}

// Simple Toaster component for use without provider
export const Toaster: React.FC = () => {
  return <ToastProvider><div /></ToastProvider>
}

'use client'

import { useState, useEffect } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { Wifi, TrendingUp, Users, Command, MapPin, Clock } from 'lucide-react'

interface LiveUpdate {
  id: string
  type: 'visitor' | 'command' | 'business' | 'performance'
  message: string
  timestamp: Date
  location?: string
  metadata?: Record<string, any>
}

const LiveDataFeed = () => {
  const [updates, setUpdates] = useState<LiveUpdate[]>([])
  const [isConnected, setIsConnected] = useState(true)

  // Generate realistic live updates
  useEffect(() => {
    const generateUpdate = (): LiveUpdate => {
      const updateTypes = [
        {
          type: 'visitor' as const,
          messages: [
            'New visitor from Copenhagen, Denmark',
            'Visitor from Stockholm, Sweden exploring projects',
            'Return visitor checking analytics dashboard',
            'User from Oslo, Norway viewing ROI calculator',
            'LinkedIn referral from business network',
          ],
          locations: ['Copenhagen', 'Stockholm', 'Oslo', 'Berlin', 'London']
        },
        {
          type: 'command' as const,
          messages: [
            'Terminal command executed: help',
            'ROI calculator demonstration viewed',
            'Analytics dashboard accessed',
            'AI demo workflow initiated',
            'Market intelligence tool used',
            'Contact information requested',
          ]
        },
        {
          type: 'business' as const,
          messages: [
            'HARKA pipeline value updated: +5K DKK',
            'New consultation request received',
            'VMS Group case study viewed',
            'Beyond Beta partnership milestone reached',
            'LinkedIn engagement: +23% weekly growth',
          ]
        },
        {
          type: 'performance' as const,
          messages: [
            'Core Web Vitals: All Green maintained',
            'Page load time: 0.8s (Excellent)',
            'Mobile performance score: 96/100',
            'SEO optimization: 98/100',
            'User engagement rate: 92%',
          ]
        }
      ]

      const selectedType = updateTypes[Math.floor(Math.random() * updateTypes.length)]
      const message = selectedType.messages[Math.floor(Math.random() * selectedType.messages.length)]

      return {
        id: `update-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
        type: selectedType.type,
        message,
        timestamp: new Date(),
        location: selectedType.type === 'visitor' ? 
          selectedType.locations?.[Math.floor(Math.random() * (selectedType.locations?.length || 1))] : 
          undefined,
      }
    }

    // Add initial updates
    const initialUpdates = Array.from({ length: 3 }, generateUpdate)
    setUpdates(initialUpdates)

    // Generate new updates periodically
    const interval = setInterval(() => {
      const newUpdate = generateUpdate()
      setUpdates(prev => {
        const updated = [newUpdate, ...prev]
        return updated.slice(0, 10) // Keep only last 10 updates
      })
    }, 5000 + Math.random() * 10000) // Every 5-15 seconds

    return () => clearInterval(interval)
  }, [])

  const getUpdateIcon = (type: LiveUpdate['type']) => {
    switch (type) {
      case 'visitor':
        return <Users className="w-4 h-4" />
      case 'command':
        return <Command className="w-4 h-4" />
      case 'business':
        return <TrendingUp className="w-4 h-4" />
      case 'performance':
        return <Wifi className="w-4 h-4" />
      default:
        return <Wifi className="w-4 h-4" />
    }
  }

  const getUpdateColor = (type: LiveUpdate['type']) => {
    switch (type) {
      case 'visitor':
        return 'text-blue-400'
      case 'command':
        return 'text-terminal-green'
      case 'business':
        return 'text-yellow-400'
      case 'performance':
        return 'text-green-400'
      default:
        return 'text-terminal-green'
    }
  }

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5, delay: 0.2 }}
      className="live-data-feed max-w-6xl mx-auto px-4"
    >
      {/* Header */}
      <div className="text-center mb-6">
        <div className="flex items-center justify-center gap-3 mb-2">
          <motion.div
            animate={{ opacity: [1, 0.3, 1] }}
            transition={{ duration: 2, repeat: Infinity }}
            className="flex items-center gap-2 text-terminal-green"
          >
            <Wifi className="w-5 h-5" />
            <span className="font-bold">LIVE DATA STREAM</span>
          </motion.div>
          <div className={`w-3 h-3 rounded-full ${isConnected ? 'bg-green-500' : 'bg-red-500'}`} />
        </div>
        <p className="text-terminal-secondary-text text-sm">
          Real-time analytics demonstrating HARKA's business intelligence capabilities
        </p>
      </div>

      {/* Live Updates Stream */}
      <div className="bg-terminal-hover border border-terminal-border rounded-lg p-6 max-h-96 overflow-hidden">
        <div className="space-y-3">
          <AnimatePresence initial={false}>
            {updates.map((update, index) => (
              <motion.div
                key={update.id}
                initial={{ opacity: 0, x: -50, height: 0 }}
                animate={{ opacity: 1, x: 0, height: 'auto' }}
                exit={{ opacity: 0, x: 50, height: 0 }}
                transition={{ duration: 0.4, delay: index * 0.05 }}
                className="flex items-start gap-3 p-3 bg-terminal-terminal-bg/50 rounded border border-terminal-border/50 hover:border-terminal-green/50 transition-all duration-300"
              >
                <div className={`${getUpdateColor(update.type)} mt-1 flex-shrink-0`}>
                  {getUpdateIcon(update.type)}
                </div>

                <div className="flex-1 min-w-0">
                  <div className="flex items-center gap-2 mb-1">
                    <span className="text-terminal-text text-sm font-medium">
                      {update.message}
                    </span>
                    {update.location && (
                      <div className="flex items-center gap-1 text-xs text-terminal-secondary-text">
                        <MapPin className="w-3 h-3" />
                        {update.location}
                      </div>
                    )}
                  </div>

                  <div className="flex items-center gap-1 text-xs text-terminal-secondary-text">
                    <Clock className="w-3 h-3" />
                    {update.timestamp.toLocaleTimeString()}
                  </div>
                </div>
              </motion.div>
            ))}
          </AnimatePresence>
        </div>

        {/* Stream Footer */}
        <div className="mt-4 pt-4 border-t border-terminal-border/50 text-center">
          <p className="text-xs text-terminal-secondary-text">
            Updates refresh every 5-15 seconds • 
            <span className="text-terminal-green"> {updates.length} </span>
            recent activities
          </p>
        </div>
      </div>

      {/* Business Metrics Bar */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.4 }}
        className="mt-6 grid grid-cols-1 md:grid-cols-4 gap-4"
      >
        <div className="bg-terminal-hover border border-terminal-border rounded p-4 text-center hover:border-terminal-green transition-all duration-300">
          <div className="text-terminal-green font-bold text-lg">
            {Math.floor(Math.random() * 5) + 12}
          </div>
          <div className="text-terminal-secondary-text text-xs">Active Visitors</div>
        </div>

        <div className="bg-terminal-hover border border-terminal-border rounded p-4 text-center hover:border-terminal-green transition-all duration-300">
          <div className="text-terminal-green font-bold text-lg">
            {Math.floor(Math.random() * 20) + 127}
          </div>
          <div className="text-terminal-secondary-text text-xs">Sessions Today</div>
        </div>

        <div className="bg-terminal-hover border border-terminal-border rounded p-4 text-center hover:border-terminal-green transition-all duration-300">
          <div className="text-terminal-green font-bold text-lg">
            4:32
          </div>
          <div className="text-terminal-secondary-text text-xs">Avg Session</div>
        </div>

        <div className="bg-terminal-hover border border-terminal-border rounded p-4 text-center hover:border-terminal-green transition-all duration-300">
          <div className="text-terminal-green font-bold text-lg">
            92%
          </div>
          <div className="text-terminal-secondary-text text-xs">Engagement</div>
        </div>
      </motion.div>

      {/* HARKA Pipeline Status */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5, delay: 0.6 }}
        className="mt-6 bg-gradient-to-r from-terminal-hover to-terminal-terminal-bg border border-terminal-border rounded-lg p-6"
      >
        <div className="text-center">
          <h3 className="text-lg font-bold text-terminal-green mb-2">
            HARKA Business Pipeline Status
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div>
              <div className="text-2xl font-bold text-terminal-green">165K DKK</div>
              <div className="text-terminal-secondary-text text-sm">Active Pipeline</div>
              <div className="text-xs text-terminal-green mt-1">+5K this week</div>
            </div>
            <div>
              <div className="text-2xl font-bold text-terminal-green">3</div>
              <div className="text-terminal-secondary-text text-sm">Qualified Leads</div>
              <div className="text-xs text-terminal-green mt-1">2 proposal stage</div>
            </div>
            <div>
              <div className="text-2xl font-bold text-terminal-green">48h</div>
              <div className="text-terminal-secondary-text text-sm">Delivery Promise</div>
              <div className="text-xs text-terminal-green mt-1">100% on-time</div>
            </div>
          </div>
        </div>
      </motion.div>
    </motion.div>
  )
}

export default LiveDataFeed

