'use client'

import { useState, useEffect, useRef, KeyboardEvent } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { useTerminalStore } from '@/store/terminalStore'
import { useAnalytics } from '@/hooks/useAnalytics'
import { commands } from '@/lib/commands'
import TerminalHeader from './terminal/TerminalHeader'
import TerminalOutput from './terminal/TerminalOutput'
import TerminalInput from './terminal/TerminalInput'

const ASCII_LOGO = `
 ███████╗██╗   ██╗███████╗███╗   ██╗     █████╗ ██╗
 ██╔════╝██║   ██║██╔════╝████╗  ██║    ██╔══██╗██║
 ███████╗██║   ██║█████╗  ██╔██╗ ██║    ███████║██║
 ╚════██║╚██╗ ██╔╝██╔══╝  ██║╚██╗██║    ██╔══██║██║
 ███████║ ╚████╔╝ ███████╗██║ ╚████║    ██║  ██║██║
 ╚══════╝  ╚═══╝  ╚══════╝╚═╝  ╚═══╝    ╚═╝  ╚═╝╚═╝
`

const WELCOME_TEXT = [
  'Welcome to SVEN AI Terminal v4.0',
  'Type \'help\' to see available commands.',
  'Type \'clear\' to clear the terminal.',
]

interface TerminalProps {
  className?: string
}

const Terminal: React.FC<TerminalProps> = ({ className = '' }) => {
  const [input, setInput] = useState('')
  const [isProcessing, setIsProcessing] = useState(false)
  const [historyIndex, setHistoryIndex] = useState(-1)

  const inputRef = useRef<HTMLInputElement>(null)
  const outputRef = useRef<HTMLDivElement>(null)

  const { 
    output, 
    commandHistory, 
    currentTheme,
    addToOutput, 
    addToHistory, 
    clearOutput,
    setTheme 
  } = useTerminalStore()

  const { trackCommand, trackTimeSpent } = useAnalytics()

  // Focus input on mount and scroll to bottom
  useEffect(() => {
    inputRef.current?.focus()

    // Track page visit
    trackTimeSpent('terminal')

    // Initialize terminal with welcome message
    if (output.length === 0) {
      addToOutput({
        type: 'ascii',
        content: ASCII_LOGO,
        timestamp: new Date().toISOString(),
      })

      WELCOME_TEXT.forEach(text => {
        addToOutput({
          type: 'system',
          content: text,
          timestamp: new Date().toISOString(),
        })
      })
    }
  }, [])

  // Auto-scroll to bottom when output changes
  useEffect(() => {
    if (outputRef.current) {
      outputRef.current.scrollTop = outputRef.current.scrollHeight
    }
  }, [output])

  // Handle keyboard input
  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
    if (isProcessing) return

    switch (e.key) {
      case 'Enter':
        if (input.trim()) {
          executeCommand(input.trim())
          setInput('')
          setHistoryIndex(-1)
        }
        break

      case 'ArrowUp':
        e.preventDefault()
        navigateHistory('up')
        break

      case 'ArrowDown':
        e.preventDefault()
        navigateHistory('down')
        break

      case 'Tab':
        e.preventDefault()
        handleAutoComplete()
        break
    }
  }

  // Navigate command history
  const navigateHistory = (direction: 'up' | 'down') => {
    if (commandHistory.length === 0) return

    let newIndex = historyIndex

    if (direction === 'up') {
      newIndex = historyIndex >= commandHistory.length - 1 ? commandHistory.length - 1 : historyIndex + 1
    } else {
      newIndex = historyIndex <= 0 ? -1 : historyIndex - 1
    }

    setHistoryIndex(newIndex)
    setInput(newIndex === -1 ? '' : commandHistory[commandHistory.length - 1 - newIndex])
  }

  // Auto-complete commands
  const handleAutoComplete = () => {
    if (!input.trim()) return

    const availableCommands = Object.keys(commands)
    const matches = availableCommands.filter(cmd => 
      cmd.toLowerCase().startsWith(input.toLowerCase())
    )

    if (matches.length === 1) {
      setInput(matches[0])
    } else if (matches.length > 1) {
      addToOutput({
        type: 'system',
        content: `Available completions: ${matches.join(', ')}`,
        timestamp: new Date().toISOString(),
      })
    }
  }

  // Execute terminal command
  const executeCommand = async (commandStr: string) => {
    setIsProcessing(true)

    // Add command to output
    addToOutput({
      type: 'command',
      content: `sven@ai:~$ ${commandStr}`,
      timestamp: new Date().toISOString(),
    })

    // Add to history
    addToHistory(commandStr)

    // Track command usage
    trackCommand(commandStr)

    // Parse command and arguments
    const [cmd, ...args] = commandStr.toLowerCase().split(' ')

    try {
      // Handle special commands
      if (cmd === 'clear') {
        clearOutput()
        // Re-add welcome message
        addToOutput({
          type: 'ascii',
          content: ASCII_LOGO,
          timestamp: new Date().toISOString(),
        })

        WELCOME_TEXT.forEach(text => {
          addToOutput({
            type: 'system',
            content: text,
            timestamp: new Date().toISOString(),
          })
        })
      } else if (cmd === 'theme' && args.length > 0) {
        const theme = args[0] as 'matrix' | 'retro' | 'cyber' | 'dark'
        setTheme(theme)
        addToOutput({
          type: 'success',
          content: `Theme changed to: ${theme}`,
          timestamp: new Date().toISOString(),
        })
      } else if (commands[cmd]) {
        // Execute command
        const result = await commands[cmd].action(args, {
          setTheme,
          currentTheme,
        })

        if (result) {
          addToOutput({
            type: 'success',
            content: result,
            timestamp: new Date().toISOString(),
          })
        }
      } else {
        // Command not found
        addToOutput({
          type: 'error',
          content: `Command not found: ${cmd}. Type 'help' for available commands.`,
          timestamp: new Date().toISOString(),
        })
      }
    } catch (error) {
      addToOutput({
        type: 'error',
        content: `Error executing command: ${error instanceof Error ? error.message : 'Unknown error'}`,
        timestamp: new Date().toISOString(),
      })
    }

    setIsProcessing(false)
    inputRef.current?.focus()
  }

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
      className={`terminal mx-auto mt-20 mb-5 max-w-[95%] bg-terminal-terminal-bg border-2 border-terminal-border rounded-lg shadow-terminal overflow-hidden ${className}`}
    >
      {/* Terminal Header */}
      <TerminalHeader />

      {/* Terminal Body */}
      <div className="terminal-body p-5 min-h-[500px] max-h-[70vh] overflow-y-auto">
        {/* Output Area */}
        <div ref={outputRef} className="output mb-5 leading-relaxed">
          <TerminalOutput output={output} />
        </div>

        {/* Input Line */}
        <div className="input-line flex items-center mt-5">
          <span className="prompt text-terminal-green mr-2.5 font-bold">
            sven@ai:~$
          </span>

          <TerminalInput
            ref={inputRef}
            value={input}
            onChange={setInput}
            onKeyDown={handleKeyDown}
            disabled={isProcessing}
            placeholder={isProcessing ? 'Processing...' : ''}
          />

          {/* Blinking Cursor */}
          <motion.span
            animate={{ opacity: [1, 0, 1] }}
            transition={{ duration: 1, repeat: Infinity }}
            className="cursor text-terminal-green ml-1 select-none"
          >
            █
          </motion.span>
        </div>

        {/* Processing Indicator */}
        <AnimatePresence>
          {isProcessing && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="flex items-center gap-2 mt-2 text-terminal-secondary-text text-sm"
            >
              <div className="animate-loading">Processing...</div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </motion.div>
  )
}

export default Terminal

import { create } from 'zustand'
import { persist } from 'zustand/middleware'

export interface OutputEntry {
  id: string
  type: 'command' | 'success' | 'error' | 'ascii' | 'system'
  content: string
  timestamp: string
}

export interface TerminalState {
  // Terminal Output
  output: OutputEntry[]
  commandHistory: string[]
  currentTheme: 'matrix' | 'retro' | 'cyber' | 'dark'

  // Analytics
  commandStats: Record<string, number>
  sessionStartTime: number
  totalCommands: number

  // Actions
  addToOutput: (entry: Omit<OutputEntry, 'id'>) => void
  addToHistory: (command: string) => void
  clearOutput: () => void
  setTheme: (theme: 'matrix' | 'retro' | 'cyber' | 'dark') => void
  incrementCommand: (command: string) => void
  resetSession: () => void
}

export const useTerminalStore = create<TerminalState>()(
  persist(
    (set, get) => ({
      // Initial state
      output: [],
      commandHistory: [],
      currentTheme: 'matrix',
      commandStats: {},
      sessionStartTime: Date.now(),
      totalCommands: 0,

      // Actions
      addToOutput: (entry) => {
        const newEntry: OutputEntry = {
          ...entry,
          id: `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
        }

        set((state) => ({
          output: [...state.output, newEntry],
        }))
      },

      addToHistory: (command) => {
        set((state) => {
          const newHistory = [...state.commandHistory, command]
          // Keep only last 50 commands
          if (newHistory.length > 50) {
            newHistory.shift()
          }
          return {
            commandHistory: newHistory,
            totalCommands: state.totalCommands + 1,
          }
        })
      },

      clearOutput: () => {
        set({ output: [] })
      },

      setTheme: (theme) => {
        set({ currentTheme: theme })

        // Apply theme to document
        if (typeof document !== 'undefined') {
          document.documentElement.classList.remove(
            'theme-matrix', 
            'theme-retro', 
            'theme-cyber', 
            'theme-dark'
          )
          document.documentElement.classList.add(`theme-${theme}`)

          // Save to localStorage
          localStorage.setItem('terminal-theme', theme)
        }
      },

      incrementCommand: (command) => {
        set((state) => ({
          commandStats: {
            ...state.commandStats,
            [command]: (state.commandStats[command] || 0) + 1,
          },
        }))
      },

      resetSession: () => {
        set({
          output: [],
          commandHistory: [],
          commandStats: {},
          sessionStartTime: Date.now(),
          totalCommands: 0,
        })
      },
    }),
    {
      name: 'terminal-storage',
      partialize: (state) => ({
        commandHistory: state.commandHistory,
        currentTheme: state.currentTheme,
        commandStats: state.commandStats,
        totalCommands: state.totalCommands,
      }),
    }
  )
)

'use client'

import { useState, useEffect, useRef } from 'react'
import { X, Send, MessageCircle } from 'lucide-react'
import { motion, AnimatePresence } from 'framer-motion'
import { useChatStore } from '@/store/chatStore'
import { useSupabase } from '@/hooks/useSupabase'
import { generateAIResponse } from '@/lib/aiResponse'

interface Message {
  id: string
  role: 'user' | 'assistant'
  content: string
  timestamp: string
}

const ChatInterface = () => {
  const [isOpen, setIsOpen] = useState(false)
  const [input, setInput] = useState('')
  const [isTyping, setIsTyping] = useState(false)

  const messagesEndRef = useRef<HTMLDivElement>(null)
  const inputRef = useRef<HTMLInputElement>(null)

  const { messages, addMessage, loadMessages } = useChatStore()
  const { saveMessage } = useSupabase()

  // Load chat history on mount
  useEffect(() => {
    loadMessages()
  }, [loadMessages])

  // Auto-scroll to bottom when messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' })
  }, [messages])

  // Focus input when chat opens
  useEffect(() => {
    if (isOpen && inputRef.current) {
      inputRef.current.focus()
    }
  }, [isOpen])

  const handleSendMessage = async () => {
    if (!input.trim() || isTyping) return

    const userMessage: Message = {
      id: `user-${Date.now()}`,
      role: 'user',
      content: input.trim(),
      timestamp: new Date().toISOString(),
    }

    // Add user message
    addMessage(userMessage)
    await saveMessage(userMessage.role, userMessage.content)

    const currentInput = input
    setInput('')
    setIsTyping(true)

    try {
      // Generate AI response
      const responseContent = await generateAIResponse(currentInput, messages)

      const assistantMessage: Message = {
        id: `assistant-${Date.now()}`,
        role: 'assistant',
        content: responseContent,
        timestamp: new Date().toISOString(),
      }

      // Add assistant message
      addMessage(assistantMessage)
      await saveMessage(assistantMessage.role, assistantMessage.content)
    } catch (error) {
      console.error('Error generating AI response:', error)

      const errorMessage: Message = {
        id: `error-${Date.now()}`,
        role: 'assistant',
        content: 'I apologize, but I encountered an error. Please try again.',
        timestamp: new Date().toISOString(),
      }

      addMessage(errorMessage)
    } finally {
      setIsTyping(false)
    }
  }

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault()
      handleSendMessage()
    }
  }

  return (
    <>
      {/* Chat Toggle Button */}
      {!isOpen && (
        <motion.button
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
          onClick={() => setIsOpen(true)}
          className="fixed bottom-6 right-6 bg-terminal-green text-terminal-terminal-bg p-4 rounded-full shadow-glow hover:shadow-lg transition-all duration-300 z-50"
          title="Open AI Chat"
        >
          <MessageCircle className="w-6 h-6" />
        </motion.button>
      )}

      {/* Chat Interface */}
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0, x: 400, y: 50 }}
            animate={{ opacity: 1, x: 0, y: 0 }}
            exit={{ opacity: 0, x: 400, y: 50 }}
            transition={{ type: 'spring', stiffness: 300, damping: 30 }}
            className="fixed right-5 top-20 w-[350px] h-[500px] bg-black/95 backdrop-blur-sm border border-terminal-border rounded-lg flex flex-col z-[999] shadow-chat lg:w-[400px] lg:h-[600px]"
          >
            {/* Chat Header */}
            <div className="chat-header p-4 border-b border-terminal-border bg-black/80 rounded-t-lg flex justify-between items-center">
              <h3 className="text-terminal-green font-bold text-lg">AI Assistant</h3>
              <button
                onClick={() => setIsOpen(false)}
                className="text-terminal-text hover:text-terminal-green hover:bg-terminal-hover w-6 h-6 rounded-full flex items-center justify-center transition-all duration-300"
                title="Close Chat"
              >
                <X className="w-4 h-4" />
              </button>
            </div>

            {/* Messages Area */}
            <div className="flex-1 p-4 overflow-y-auto">
              <div className="space-y-4">
                {messages.length === 0 && (
                  <div className="text-terminal-secondary-text text-center text-sm">
                    👋 Hello! I'm SVEN AI's assistant. Ask me anything about AI consulting, projects, or business automation!
                  </div>
                )}

                {messages.map((message) => (
                  <motion.div
                    key={message.id}
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    className={`chat-message ${message.role} ${
                      message.role === 'user' ? 'ml-8' : 'mr-8'
                    }`}
                  >
                    <div className="message-role text-xs text-terminal-green mb-1 font-bold">
                      {message.role === 'user' ? 'You' : 'SVEN AI'}
                    </div>
                    <div
                      className={`message-content p-3 rounded-lg text-sm leading-relaxed border ${
                        message.role === 'user'
                          ? 'bg-terminal-green/10 border-terminal-green text-terminal-text ml-auto'
                          : 'bg-terminal-hover border-terminal-border text-terminal-text'
                      }`}
                    >
                      {message.content}
                    </div>
                  </motion.div>
                ))}

                {/* Typing Indicator */}
                {isTyping && (
                  <motion.div
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="chat-message assistant mr-8"
                  >
                    <div className="message-role text-xs text-terminal-green mb-1 font-bold">
                      SVEN AI
                    </div>
                    <div className="message-content bg-terminal-hover border border-terminal-border p-3 rounded-lg text-sm">
                      <div className="flex items-center gap-1">
                        <div className="animate-loading">Thinking</div>
                        <motion.div
                          animate={{ opacity: [0, 1, 0] }}
                          transition={{ duration: 1, repeat: Infinity }}
                        >
                          ...
                        </motion.div>
                      </div>
                    </div>
                  </motion.div>
                )}

                <div ref={messagesEndRef} />
              </div>
            </div>

            {/* Input Area */}
            <div className="chat-input-container p-4 border-t border-terminal-border bg-black/80 rounded-b-lg">
              <div className="flex gap-2">
                <input
                  ref={inputRef}
                  type="text"
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="Ask me anything..."
                  disabled={isTyping}
                  className="flex-1 bg-terminal-hover border border-terminal-border rounded px-3 py-2 text-terminal-text text-sm font-mono outline-none focus:border-terminal-green transition-colors disabled:opacity-50"
                />
                <button
                  onClick={handleSendMessage}
                  disabled={!input.trim() || isTyping}
                  className="bg-terminal-green text-terminal-terminal-bg px-4 py-2 rounded font-bold text-sm hover:bg-terminal-green/90 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-300 hover:shadow-glow"
                >
                  <Send className="w-4 h-4" />
                </button>
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </>
  )
}

