import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@supabase/supabase-js'

// Initialize Supabase client for server-side operations
const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL!
const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY || process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
const supabase = createClient(supabaseUrl, supabaseServiceKey)

interface AnalyticsData {
  command?: string
  event_type?: string
  metadata?: Record<string, any>
  user_agent?: string
  ip_address?: string
  timestamp?: string
}

// GET - Retrieve analytics data
export async function GET(request: NextRequest) {
  const { searchParams } = new URL(request.url)
  const timeframe = searchParams.get('timeframe') || '24h'
  const command = searchParams.get('command')

  try {
    // Calculate time range
    const hoursBack = timeframe === '24h' ? 24 : timeframe === '7d' ? 168 : 720
    const cutoffTime = new Date(Date.now() - hoursBack * 60 * 60 * 1000).toISOString()

    let query = supabase
      .from('analytics')
      .select('*')
      .gte('created_at', cutoffTime)
      .order('created_at', { ascending: false })

    // Filter by specific command if requested
    if (command) {
      query = query.eq('command', command)
    }

    const { data, error } = await query.limit(1000)

    if (error) {
      console.error('Analytics query error:', error)
      return NextResponse.json({ error: error.message }, { status: 500 })
    }

    // Process analytics data
    const commandCounts: Record<string, number> = {}
    const hourlyData: Record<string, { visitors: number; commands: number }> = {}
    const geoData: Record<string, number> = {}

    data?.forEach(record => {
      // Command counts
      if (record.command) {
        commandCounts[record.command] = (commandCounts[record.command] || 0) + 1
      }

      // Hourly data (simplified)
      const hour = new Date(record.created_at).getHours()
      const hourKey = `${hour}:00`
      if (!hourlyData[hourKey]) {
        hourlyData[hourKey] = { visitors: 0, commands: 0 }
      }
      hourlyData[hourKey].commands += 1

      // Geographic data (simplified - in production, use proper IP geolocation)
      const country = record.metadata?.country || 'Unknown'
      geoData[country] = (geoData[country] || 0) + 1
    })

    // Generate response
    const response = {
      success: true,
      timeframe,
      totalEvents: data?.length || 0,
      commandCounts,
      hourlyData,
      geoData,
      topCommands: Object.entries(commandCounts)
        .sort(([,a], [,b]) => b - a)
        .slice(0, 10)
        .map(([command, count]) => ({ command, count })),
    }

    return NextResponse.json(response)

  } catch (error) {
    console.error('Analytics API error:', error)
    return NextResponse.json(
      { error: 'Internal server error' }, 
      { status: 500 }
    )
  }
}

// POST - Track new analytics event
export async function POST(request: NextRequest) {
  try {
    const body: AnalyticsData = await request.json()

    // Get client IP and User-Agent
    const forwarded = request.headers.get('x-forwarded-for')
    const ip = forwarded ? forwarded.split(',')[0] : request.headers.get('x-real-ip') || 'unknown'
    const userAgent = request.headers.get('user-agent') || 'unknown'

    // Prepare analytics record
    const analyticsRecord = {
      command: body.command,
      event_type: body.event_type || 'command',
      metadata: {
        ...body.metadata,
        user_agent: userAgent,
        ip_address: ip,
        timestamp: body.timestamp || new Date().toISOString(),
      },
    }

    // Save to Supabase
    const { data, error } = await supabase
      .from('analytics')
      .insert([analyticsRecord])
      .select()

    if (error) {
      console.error('Analytics insert error:', error)
      return NextResponse.json({ error: error.message }, { status: 500 })
    }

    return NextResponse.json({ 
      success: true, 
      message: 'Analytics tracked successfully',
      id: data?.[0]?.id 
    })

  } catch (error) {
    console.error('Analytics POST error:', error)
    return NextResponse.json(
      { error: 'Invalid request body' }, 
      { status: 400 }
    )
  }
}

// OPTIONS - Handle CORS preflight
export async function OPTIONS() {
  return new NextResponse(null, {
    status: 200,
    headers: {
      'Access-Control-Allow-Origin': '*',
      'Access-Control-Allow-Methods': 'GET, POST, OPTIONS',
      'Access-Control-Allow-Headers': 'Content-Type, Authorization',
    },
  })
}

