private REPLICATE_API_TOKEN = 'xxxxx';

/**
   * Generates an image using Recraft V3 model via Replicate API
   * @param prompt {string} The text description of the image to generate
   * @param size {string} The size of the generated image (e.g. "1365x1024")
   * @returns {Promise<Response>} A Response object containing the generated image URL
   */
  async generateImage(prompt: string, size: string = "1365x1024"): Promise<Response> {
    // Step 1: Create the prediction
    const response = await fetch('https://api.replicate.com/v1/models/recraft-ai/recraft-v3/predictions', {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${this.REPLICATE_API_TOKEN}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        input: {
          prompt: prompt,
          size: size
        }
      })
    });

    if (!response.ok) {
      return new Response(`Failed to start image generation: ${await response.text()}`, {
        status: 500,
        headers: { 'Content-Type': 'text/plain' }
      });
    }

    // Step 2: Get the prediction details and poll until complete
    const prediction = await response.json() as ReplicatePrediction;
    const getUrl = prediction.urls?.get;

    if (!getUrl) {
      return new Response('No URL to check prediction status', {
        status: 500,
        headers: { 'Content-Type': 'text/plain' }
      });
    }

    // Step 3: Poll the prediction until it's complete
    let attempts = 0;
    const maxAttempts = 30; // 30 seconds timeout

    while (attempts < maxAttempts) {
      const statusResponse = await fetch(getUrl, {
        headers: {
          'Authorization': `Bearer ${this.REPLICATE_API_TOKEN}`
        }
      });

      if (!statusResponse.ok) {
        return new Response(`Failed to check prediction status: ${await statusResponse.text()}`, {
          status: 500,
          headers: { 'Content-Type': 'text/plain' }
        });
      }

      const result = await statusResponse.json() as ReplicatePrediction;

      // Check if the prediction is complete
      if (result.status === 'succeeded' && result.output) {
        return new Response(result.output, {
          headers: {
            'Content-Type': 'text/plain',
            'Access-Control-Allow-Origin': '*'
          }
        });
      }

      // Check if the prediction failed
      if (result.status === 'failed') {
        return new Response(`Image generation failed: ${result.error || 'Unknown error'}`, {
          status: 500,
          headers: { 'Content-Type': 'text/plain' }
        });
      }

      // Wait 1 second before next attempt
      await new Promise(resolve => setTimeout(resolve, 1000));
      attempts++;
    }

    return new Response('Timeout waiting for image generation', {
      status: 504,
      headers: { 'Content-Type': 'text/plain' }
    });
  }


