### Day 3-4: Supabase Setup

```sql
-- Supabase SQL Editor: Create tables with RLS

-- Contacts table
CREATE TABLE contacts (
  id UUID DEFAULT gen_random_uuid() PRIMARY KEY,
  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE,
  linkedin_url TEXT UNIQUE,
  first_name TEXT NOT NULL,
  last_name TEXT NOT NULL,
  email TEXT,
  phone TEXT,
  company JSONB,
  current_position JSONB,
  metadata JSONB DEFAULT '{}',
  tags TEXT[] DEFAULT '{}',
  segment TEXT CHECK (segment IN ('automation', 'ai_consulting', 'workshop', 'education')),
  engagement_score INTEGER DEFAULT 0,
  last_interaction TIMESTAMPTZ,
  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW()
);

-- Relationships table for network mapping
CREATE TABLE relationships (
  id UUID DEFAULT gen_random_uuid() PRIMARY KEY,
  contact_a_id UUID REFERENCES contacts(id) ON DELETE CASCADE,
  contact_b_id UUID REFERENCES contacts(id) ON DELETE CASCADE,
  relationship_type TEXT,
  strength INTEGER CHECK (strength BETWEEN 1 AND 10),
  source TEXT DEFAULT 'linkedin',
  metadata JSONB DEFAULT '{}',
  created_at TIMESTAMPTZ DEFAULT NOW(),
  UNIQUE(contact_a_id, contact_b_id)
);

-- Deals/Pipeline
CREATE TABLE deals (
  id UUID DEFAULT gen_random_uuid() PRIMARY KEY,
  contact_id UUID REFERENCES contacts(id),
  pipeline TEXT NOT NULL,
  stage TEXT NOT NULL,
  value DECIMAL(10,2),
  probability INTEGER,
  expected_close DATE,
  metadata JSONB DEFAULT '{}',
  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW()
);

-- Enable Row Level Security
ALTER TABLE contacts ENABLE ROW LEVEL SECURITY;
ALTER TABLE relationships ENABLE ROW LEVEL SECURITY;
ALTER TABLE deals ENABLE ROW LEVEL SECURITY;

-- Create policies
CREATE POLICY "Users can view own contacts" ON contacts
  FOR ALL USING (auth.uid() = user_id);

CREATE POLICY "Users can view own relationships" ON relationships
  FOR ALL USING (
    EXISTS (
      SELECT 1 FROM contacts 
      WHERE contacts.id IN (contact_a_id, contact_b_id) 
      AND contacts.user_id = auth.uid()
    )
  );

-- Create indexes for performance
CREATE INDEX idx_contacts_user_id ON contacts(user_id);
CREATE INDEX idx_contacts_linkedin ON contacts(linkedin_url);
CREATE INDEX idx_relationships_contacts ON relationships(contact_a_id, contact_b_id);
CREATE INDEX idx_deals_contact ON deals(contact_id);
