### Day 5: Bright Data MCP Integration

```typescript
// lib/brightdata-mcp.ts
import { exec } from 'child_process';
import { promisify } from 'util';

const execAsync = promisify(exec);

export class BrightDataMCP {
  private apiToken: string;
  private browserAuth: string;

  constructor() {
    this.apiToken = process.env.BRIGHTDATA_API_TOKEN!;
    this.browserAuth = process.env.BRIGHTDATA_BROWSER_AUTH!;
  }

  async scrapeLinkedInProfile(profileUrl: string) {
    const command = `
      API_TOKEN="${this.apiToken}" 
      BROWSER_AUTH="${this.browserAuth}" 
      npx -y @brightdata/mcp scrape-profile "${profileUrl}"
    `;

    try {
      const { stdout } = await execAsync(command);
      return JSON.parse(stdout);
    } catch (error) {
      console.error('Bright Data MCP error:', error);
      throw error;
    }
  }

  async scrapeLinkedInConnections(profileUrl: string, depth: number = 1) {
    const command = `
      API_TOKEN="${this.apiToken}" 
      BROWSER_AUTH="${this.browserAuth}" 
      npx -y @brightdata/mcp scrape-connections "${profileUrl}" --depth ${depth}
    `;

    try {
      const { stdout } = await execAsync(command);
      return JSON.parse(stdout);
    } catch (error) {
      console.error('Bright Data MCP connections error:', error);
      throw error;
    }
  }

  async searchLinkedIn(query: string, filters?: any) {
    const filterString = filters ? `--filters '${JSON.stringify(filters)}'` : '';
    const command = `
      API_TOKEN="${this.apiToken}" 
      BROWSER_AUTH="${this.browserAuth}" 
      npx -y @brightdata/mcp search-linkedin "${query}" ${filterString}
    `;

    try {
      const { stdout } = await execAsync(command);
      return JSON.parse(stdout);
    } catch (error) {
      console.error('Bright Data MCP search error:', error);
      throw error;
    }
  }
}

// API Route: app/api/linkedin/scrape/route.ts
import { NextResponse } from 'next/server';
import { createRouteHandlerClient } from '@supabase/auth-helpers-nextjs';
import { cookies } from 'next/headers';
import { BrightDataMCP } from '@/lib/brightdata-mcp';

export async function POST(request: Request) {
  const supabase = createRouteHandlerClient({ cookies });
  const { data: { user } } = await supabase.auth.getUser();

  if (!user) {
    return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
  }

  const { profileUrl, scrapeConnections } = await request.json();
  const mcp = new BrightDataMCP();

  try {
    // Scrape profile
    const profileData = await mcp.scrapeLinkedInProfile(profileUrl);

    // Create contact in Supabase
    const { data: contact, error } = await supabase
      .from('contacts')
      .upsert({
        user_id: user.id,
        linkedin_url: profileUrl,
        first_name: profileData.firstName,
        last_name: profileData.lastName,
        email: profileData.email,
        company: profileData.company,
        current_position: profileData.currentPosition,
        metadata: profileData
      })
      .select()
      .single();

    if (error) throw error;

    // Scrape connections if requested
    if (scrapeConnections && contact) {
      const connections = await mcp.scrapeLinkedInConnections(profileUrl, 1);

      // Process connections in parallel
      const connectionPromises = connections.map(async (conn: any) => {
        // Create or update connected contact
        const { data: connectedContact } = await supabase
          .from('contacts')
          .upsert({
            user_id: user.id,
            linkedin_url: conn.profileUrl,
            first_name: conn.firstName,
            last_name: conn.lastName,
            company: conn.company,
            current_position: conn.position
          })
          .select()
          .single();

        if (connectedContact) {
          // Create relationship
          await supabase
            .from('relationships')
            .upsert({
              contact_a_id: contact.id,
              contact_b_id: connectedContact.id,
              relationship_type: 'linkedin_connection',
              strength: conn.connectionDegree === '1st' ? 8 : 5,
              metadata: conn
            });
        }
      });

      await Promise.all(connectionPromises);
    }

    return NextResponse.json({ contact, message: 'Profile scraped successfully' });
  } catch (error) {
    console.error('Scraping error:', error);
    return NextResponse.json(
      { error: 'Failed to scrape LinkedIn profile' },
      { status: 500 }
    );
  }
}
