// components/relationship-graph.tsx
'use client';

import { useEffect, useRef } from 'react';
import * as d3 from 'd3';

export function RelationshipGraph({ relationships }: any) {
  const svgRef = useRef<SVGSVGElement>(null);

  useEffect(() => {
    if (!svgRef.current || !relationships.length) return;

    // D3.js force-directed graph
    const width = 800;
    const height = 600;

    const svg = d3.select(svgRef.current)
      .attr('width', width)
      .attr('height', height);

    // Process data for D3
    const nodes = new Map();
    const links = relationships.map((rel: any) => {
      nodes.set(rel.contact_a_id, { id: rel.contact_a_id });
      nodes.set(rel.contact_b_id, { id: rel.contact_b_id });
      return {
        source: rel.contact_a_id,
        target: rel.contact_b_id,
        strength: rel.strength
      };
    });

    const simulation = d3.forceSimulation(Array.from(nodes.values()))
      .force('link', d3.forceLink(links).id((d: any) => d.id))
      .force('charge', d3.forceManyBody().strength(-300))
      .force('center', d3.forceCenter(width / 2, height / 2));

    // Render graph
    const link = svg.append('g')
      .selectAll('line')
      .data(links)
      .enter().append('line')
      .attr('stroke-width', (d: any) => Math.sqrt(d.strength))
      .attr('stroke', '#999');

    const node = svg.append('g')
      .selectAll('circle')
      .data(Array.from(nodes.values()))
      .enter().append('circle')
      .attr('r', 10)
      .attr('fill', '#4F46E5');

    simulation.on('tick', () => {
      link
        .attr('x1', (d: any) => d.source.x)
        .attr('y1', (d: any) => d.source.y)
        .attr('x2', (d: any) => d.target.x)
        .attr('y2', (d: any) => d.target.y);

      node
        .attr('cx', (d: any) => d.x)
        .attr('cy', (d: any) => d.y);
    });

    return () => {
      simulation.stop();
    };
  }, [relationships]);

  return (
    <div className="bg-white p-6 rounded-lg shadow">
      <h2 className="text-xl font-semibold mb-4">Relationship Network</h2>
      <svg ref={svgRef} className="w-full" />
    </div>
  );
}
