```typescript
// components/pipeline-kanban.tsx
'use client';

import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';
import { Card } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { useState } from 'react';
import { createClientComponentClient } from '@supabase/auth-helpers-nextjs';

const PIPELINES = {
  automation: ['Discovery', 'Proposal', 'Negotiation', 'Contract', 'Delivery'],
  ai_consulting: ['Initial Contact', 'Needs Analysis', 'Solution Design', 'Agreement', 'Implementation'],
  workshop: ['Lead', 'Qualified', 'Proposal Sent', 'Booked', 'Completed'],
  education: ['Inquiry', 'Assessment', 'Curriculum Design', 'Contract', 'Ongoing']
};

export function PipelineKanban({ pipeline, deals, onUpdate }: any) {
  const supabase = createClientComponentClient();
  const stages = PIPELINES[pipeline as keyof typeof PIPELINES];

  const handleDragEnd = async (result: any) => {
    if (!result.destination) return;

    const dealId = result.draggableId;
    const newStage = stages[result.destination.index];

    // Update in database
    await supabase
      .from('deals')
      .update({ stage: newStage })
      .eq('id', dealId);

    onUpdate();
  };

  return (
    <DragDropContext onDragEnd={handleDragEnd}>
      <div className="flex gap-4 overflow-x-auto pb-4">
        {stages.map((stage, index) => (
          <Droppable key={stage} droppableId={`${index}`}>
            {(provided) => (
              <div
                ref={provided.innerRef}
                {...provided.droppableProps}
                className="bg-gray-100 rounded-lg p-4 min-w-[300px]"
              >
                <h3 className="font-semibold mb-4">{stage}</h3>
                {deals
                  .filter((deal: any) => deal.stage === stage)
                  .map((deal: any, index: number) => (
                    <Draggable key={deal.id} draggableId={deal.id} index={index}>
                      {(provided) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                        >
                          <Card className="mb-2 p-3">
                            <h4 className="font-medium">{deal.contact.name}</h4>
                            <p className="text-sm text-gray-600">
                              {deal.value.toLocaleString('da-DK', {
                                style: 'currency',
                                currency: 'DKK'
                              })}
                            </p>
                            <Badge variant="secondary" className="mt-2">
                              {deal.probability}% probability
                            </Badge>
                          </Card>
                        </div>
                      )}
                    </Draggable>
                  ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        ))}
      </div>
    </DragDropContext>
  );
}
