import { Configuration, OpenAIApi } from 'openai';

const openai = new OpenAIApi(new Configuration({
  apiKey: process.env.OPENAI_API_KEY
}));

export async function analyzeRelationships(contacts: any[], relationships: any[]) {
  const prompt = `
    Analyze this professional network data and provide insights:

    Contacts: ${JSON.stringify(contacts.slice(0, 50))}
    Relationships: ${JSON.stringify(relationships.slice(0, 100))}

    Provide:
    1. Key influencers in the network
    2. Potential cross-service opportunities
    3. Warm introduction paths
    4. Industry clusters
    5. Engagement recommendations
  `;

  const response = await openai.createChatCompletion({
    model: 'gpt-4',
    messages: [{ role: 'user', content: prompt }],
    temperature: 0.7
  });

  return JSON.parse(response.data.choices[0].message.content);
}

// API Route: app/api/insights/route.ts
export async function GET(request: Request) {
  const supabase = createServerComponentClient({ cookies });

