  // Fetch data for analysis
  const { data: contacts } = await supabase
    .from('contacts')
    .select(`
      *,
      deals(*)
    `);

  // Simple rule-based opportunity detection
  const opportunities = [];

  contacts?.forEach(contact => {
    // Cross-sell opportunity
    if (contact.deals?.length === 1 && contact.engagement_score > 70) {
      opportunities.push({
        title: `Cross-sell opportunity: ${contact.first_name} ${contact.last_name}`,
        description: `Has only used ${contact.deals[0].pipeline} service. High engagement score suggests readiness for additional services.`,
        contact_id: contact.id,
        type: 'cross_sell'
      });
    }

    // Workshop opportunity
    if (contact.company?.employee_count > 10 && !contact.deals?.some(d => d.pipeline === 'workshop')) {
      opportunities.push({
        title: `Workshop opportunity: ${contact.company.name}`,
        description: `Company size (${contact.company.employee_count} employees) makes them ideal for Harka workshop.`,
        contact_id: contact.id,
        type: 'new_service'
      });
    }
  });

  return NextResponse.json(opportunities);
}
