### Interactive Network Graph

```typescript
// components/NetworkGraph.tsx
'use client';

import { useEffect, useRef } from 'react';
import * as d3 from 'd3';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';

interface NetworkGraphProps {
  contacts: any[];
  relationships: any[];
  onNodeClick?: (contact: any) => void;
}

export function NetworkGraph({ contacts, relationships, onNodeClick }: NetworkGraphProps) {
  const svgRef = useRef<SVGSVGElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (!svgRef.current || !contacts.length) return;

    const width = containerRef.current?.clientWidth || 800;
    const height = 600;

    // Clear previous graph
    d3.select(svgRef.current).selectAll('*').remove();

    const svg = d3.select(svgRef.current)
      .attr('viewBox', `0 0 ${width} ${height}`)
      .attr('preserveAspectRatio', 'xMidYMid meet');

    // Create zoom behavior
    const zoom = d3.zoom()
      .scaleExtent([0.1, 10])
      .on('zoom', (event) => {
        g.attr('transform', event.transform);
      });

    svg.call(zoom as any);

    const g = svg.append('g');

    // Prepare data
    const nodes = contacts.map(contact => ({
      id: contact.id,
      name: `${contact.first_name} ${contact.last_name}`,
      company: contact.company?.name,
      influence: contact.influence_score || 0,
      segment: contact.segment
    }));

    const links = relationships.map(rel => ({
      source: rel.contact_a_id,
      target: rel.contact_b_id,
      strength: rel.strength,
      degree: rel.degree
    }));

    // Color scale for segments
    const colorScale = d3.scaleOrdinal()
      .domain(['automation', 'ai_consulting', 'workshop', 'education'])
      .range(['#3B82F6', '#10B981', '#F59E0B', '#EF4444']);

    // Size scale for influence
    const sizeScale = d3.scaleLinear()
      .domain([0, d3.max(nodes, d => d.influence) || 1000])
      .range([5, 20]);

    // Create force simulation
    const simulation = d3.forceSimulation(nodes)
      .force('link', d3.forceLink(links)
        .id((d: any) => d.id)
        .distance(d => 100 - (d.strength * 5))
      )
      .force('charge', d3.forceManyBody().strength(-300))
      .force('center', d3.forceCenter(width / 2, height / 2))
      .force('collision', d3.forceCollide().radius(d => sizeScale(d.influence) + 5));

    // Create links
    const link = g.append('g')
      .selectAll('line')
      .data(links)
      .enter().append('line')
      .attr('stroke', '#999')
      .attr('stroke-opacity', d => d.strength / 10)
      .attr('stroke-width', d => Math.sqrt(d.strength));

    // Create nodes
    const node = g.append('g')
      .selectAll('g')
      .data(nodes)
      .enter().append('g')
      .call(d3.drag()
        .on('start', dragstarted)
        .on('drag', dragged)
        .on('end', dragended) as any
      );

    // Add circles
    node.append('circle')
      .attr('r', d => sizeScale(d.influence))
      .attr('fill', d => colorScale(d.segment) as string)
      .attr('stroke', '#fff')
      .attr('stroke-width', 2);

    // Add labels
    node.append('text')
      .text(d => d.name)
      .attr('font-size', '10px')
      .attr('dx', d => sizeScale(d.influence) + 5)
      .attr('dy', 3);

    // Add tooltips
    const tooltip = d3.select('body').append('div')
      .attr('class', 'tooltip')
      .style('opacity', 0)
      .style('position', 'absolute')
      .style('background', 'rgba(0,0,0,0.8)')
      .style('color', 'white')
      .style('padding', '8px')
      .style('border-radius', '4px')
      .style('font-size', '12px');

    node.on('mouseover', (event, d) => {
      tooltip.transition().duration(200).style('opacity', .9);
      tooltip.html(`
        <strong>${d.name}</strong><br/>
        ${d.company || 'No company'}<br/>
        Influence: ${d.influence}<br/>
        Segment: ${d.segment}
      `)
        .style('left', (event.pageX + 10) + 'px')
        .style('top', (event.pageY - 28) + 'px');
    })
    .on('mouseout', () => {
      tooltip.transition().duration(500).style('opacity', 0);
    })
    .on('click', (event, d) => {
      if (onNodeClick) onNodeClick(d);
    });

    // Update positions on tick
    simulation.on('tick', () => {
      link
        .attr('x1', (d: any) => d.source.x)
        .attr('y1', (d: any) => d.source.y)
        .attr('x2', (d: any) => d.target.x)
        .attr('y2', (d: any) => d.target.y);

      node.attr('transform', (d: any) => `translate(${d.x},${d.y})`);
    });

    // Drag functions
    function dragstarted(event: any, d: any) {
      if (!event.active) simulation.alphaTarget(0.3).restart();
      d.fx = d.x;
      d.fy = d.y;
    }

    function dragged(event: any, d: any) {
      d.fx = event.x;
      d.fy = event.y;
    }

    function dragended(event: any, d: any) {
      if (!event.active) simulation.alphaTarget(0);
      d.fx = null;
      d.fy = null;
    }

    // Cleanup
    return () => {
      simulation.stop();
      tooltip.remove();
    };
  }, [contacts, relationships, onNodeClick]);

  return (
    <Card className="p-6">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-xl font-semibold">Relationship Network</h2>
        <div className="flex gap-2">
          <Badge variant="secondary" className="bg-blue-100">
            Automation
          </Badge>
          <Badge variant="secondary" className="bg-green-100">
            AI Consulting
          </Badge>
          <Badge variant="secondary" className="bg-amber-100">
            Workshop
          </Badge>
          <Badge variant="secondary" className="bg-red-100">
            Education
          </Badge>
        </div>
      </div>
      <div ref={containerRef} className="relative">
        <svg ref={svgRef} className="w-full h-[600px]" />
      </div>
    </Card>
  );
}
