### Warm Path Finder

```typescript
// components/WarmPathFinder.tsx
'use client';

import { useState } from 'react';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { ArrowRight, Users } from 'lucide-react';

export function WarmPathFinder({ contacts, warmPaths }: any) {
  const [fromContact, setFromContact] = useState('');
  const [toContact, setToContact] = useState('');
  const [path, setPath] = useState<any>(null);

  const findPath = () => {
    const foundPath = warmPaths.find(
      (p: any) => 
        (p.from_contact_id === fromContact && p.to_contact_id === toContact) ||
        (p.from_contact_id === toContact && p.to_contact_id === fromContact)
    );
    setPath(foundPath);
  };

  return (
    <Card className="p-6">
      <h2 className="text-xl font-semibold mb-4 flex items-center gap-2">
        <Users className="h-5 w-5" />
        Warm Introduction Finder
      </h2>

      <div className="space-y-4">
        <div className="grid grid-cols-2 gap-4">
          <Select value={fromContact} onValueChange={setFromContact}>
            <SelectTrigger>
              <SelectValue placeholder="From contact..." />
            </SelectTrigger>
            <SelectContent>
              {contacts.map((contact: any) => (
                <SelectItem key={contact.id} value={contact.id}>
                  {contact.first_name} {contact.last_name}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>

          <Select value={toContact} onValueChange={setToContact}>
            <SelectTrigger>
              <SelectValue placeholder="To contact..." />
            </SelectTrigger>
            <SelectContent>
              {contacts.map((contact: any) => (
                <SelectItem key={contact.id} value={contact.id}>
                  {contact.first_name} {contact.last_name}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        <Button 
          onClick={findPath} 
          disabled={!fromContact || !toContact || fromContact === toContact}
          className="w-full"
        >
          Find Connection Path
        </Button>

        {path && (
          <div className="mt-6 p-4 bg-green-50 rounded-lg">
            <h3 className="font-semibold mb-2">
              Connection Path (Strength: {path.path_strength}/10)
            </h3>
            <div className="flex items-center gap-2">
              {path.path_contacts.map((contactId: string, idx: number) => {
                const contact = contacts.find((c: any) => c.id === contactId);
                return (
                  <div key={idx} className="flex items-center gap-2">
                    <div className="text-sm">
                      {contact?.first_name} {contact?.last_name}
                    </div>
                    {idx < path.path_contacts.length - 1 && (
                      <ArrowRight className="h-4 w-4 text-gray-400" />
                    )}
                  </div>
                );
              })}
            </div>
            <Button className="mt-4" variant="outline">
              Request Introduction
            </Button>
          </div>
        )}
      </div>
    </Card>
  );
}
