// app/api/relationships/deep-scan/route.ts
import { NextResponse } from 'next/server';
import { createRouteHandlerClient } from '@supabase/auth-helpers-nextjs';
import { cookies } from 'next/headers';
import { RelationshipMapper } from '@/services/RelationshipMapper';

export async function POST(request: Request) {
  const supabase = createRouteHandlerClient({ cookies });
  const { data: { user } } = await supabase.auth.getUser();

  if (!user) {
    return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
  }

  const { profileUrl, depth = 2 } = await request.json();
  const mapper = new RelationshipMapper();

  try {
    const result = await mapper.deepScanProfile(profileUrl, user.id, depth);
    return NextResponse.json(result);
  } catch (error) {
    console.error('Deep scan error:', error);
    return NextResponse.json(
      { error: 'Failed to perform deep scan' },
      { status: 500 }
    );
  }
}

// app/api/relationships/influence/route.ts
export async function POST(request: Request) {
  const supabase = createRouteHandlerClient({ cookies });
  const { data: { user } } = await supabase.auth.getUser();

  if (!user) {
    return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
  }

  const mapper = new RelationshipMapper();

  try {
    await mapper.calculateInfluenceScores(user.id);

    // Return top influencers
    const { data: influencers } = await supabase
      .from('contacts')
      .select('*')
      .eq('user_id', user.id)
      .order('influence_score', { ascending: false })
      .limit(10);

    return NextResponse.json({ influencers });
  } catch (error) {
    console.error('Influence calculation error:', error);
    return NextResponse.json(
      { error: 'Failed to calculate influence scores' },
      { status: 500 }
    );
  }
}

// app/api/relationships/warm-paths/route.ts
export async function GET(request: Request) {
  const supabase = createRouteHandlerClient({ cookies });
  const { data: { user } } = await supabase.auth.getUser();

  if (!user) {
    return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
  }

  const { searchParams } = new URL(request.url);
  const fromId = searchParams.get('from');
  const toId = searchParams.get('to');

  let query = supabase
    .from('warm_paths')
    .select(`
      *,
      from_contact:contacts!from_contact_id(*),
      to_contact:contacts!to_contact_id(*)
    `)
    .eq('user_id', user.id);

  if (fromId && toId) {
    query = query.or(`from_contact_id.eq.${fromId},to_contact_id.eq.${toId}`);
  }

  const { data: paths } = await query.limit(20);

  return NextResponse.json({ paths });
}
