#!/usr/bin/env python3
"""
Seven Productivity Analytics Engine
Provides insights, metrics, and performance analysis
"""

import json
from datetime import datetime, timedelta
from typing import Dict, List
import statistics

class ProductivityAnalytics:
    def __init__(self):
        self.metrics = {}
        self.insights = {}

    def analyze_task_completion(self, issues: List[Dict]) -> Dict:
        """Analyze task completion patterns"""
        if not issues:
            return {"error": "No issues to analyze"}

        # Completion rate by project
        projects = {}
        for issue in issues:
            project_labels = [label['name'] for label in issue.get('labels', []) 
                            if label['name'] in ['SEVEN', 'ALLIGN', 'HARKA', 'Atlas']]

            for project in project_labels:
                if project not in projects:
                    projects[project] = {'total': 0, 'completed': 0}
                projects[project]['total'] += 1
                if issue['state'] == 'closed':
                    projects[project]['completed'] += 1

        # Calculate completion rates
        completion_rates = {}
        for project, stats in projects.items():
            completion_rates[project] = {
                'total': stats['total'],
                'completed': stats['completed'],
                'rate': round((stats['completed'] / stats['total']) * 100, 1) if stats['total'] > 0 else 0
            }

        return {
            'completion_rates': completion_rates,
            'overall_rate': round(sum(stats['completed'] for stats in projects.values()) / 
                                sum(stats['total'] for stats in projects.values()) * 100, 1) if projects else 0
        }

    def analyze_velocity_trends(self, issues: List[Dict]) -> Dict:
        """Analyze task completion velocity over time"""
        # Group issues by week
        weekly_data = {}
        for issue in issues:
            if issue['state'] == 'closed' and issue.get('closed_at'):
                closed_date = datetime.fromisoformat(issue['closed_at'].replace('Z', '+00:00'))
                week_key = closed_date.strftime("%Y-W%U")

                if week_key not in weekly_data:
                    weekly_data[week_key] = {'count': 0, 'issues': []}
                weekly_data[week_key]['count'] += 1
                weekly_data[week_key]['issues'].append(issue)

        # Calculate velocity trend
        weeks = sorted(weekly_data.keys())
        velocities = [weekly_data[week]['count'] for week in weeks]

        trend = "stable"
        if len(velocities) > 1:
            if velocities[-1] > velocities[0]:
                trend = "increasing"
            elif velocities[-1] < velocities[0]:
                trend = "decreasing"

        return {
            'weekly_completion': weekly_data,
            'average_weekly_velocity': round(statistics.mean(velocities), 1) if velocities else 0,
            'trend': trend,
            'current_week_velocity': velocities[-1] if velocities else 0
        }

    def identify_bottlenecks(self, issues: List[Dict]) -> Dict:
        """Identify potential bottlenecks and blockers"""
        bottlenecks = {
            'long_running_tasks': [],
            'high_priority_open': [],
            'label_distribution': {}
        }

        now = datetime.now()

        for issue in issues:
            if issue['state'] == 'open':
                created_date = datetime.fromisoformat(issue['created_at'].replace('Z', '+00:00'))
                days_open = (now - created_date).days

                # Long running tasks (open > 7 days)
                if days_open > 7:
                    bottlenecks['long_running_tasks'].append({
                        'title': issue['title'],
                        'days_open': days_open,
                        'url': issue['html_url']
                    })

                # High priority open tasks
                labels = [label['name'] for label in issue.get('labels', [])]
                if any('high-priority' in label for label in labels):
                    bottlenecks['high_priority_open'].append({
                        'title': issue['title'],
                        'days_open': days_open,
                        'url': issue['html_url']
                    })

                # Label distribution
                for label in labels:
                    bottlenecks['label_distribution'][label] = bottlenecks['label_distribution'].get(label, 0) + 1

        return bottlenecks

    def generate_insights(self, completion_data: Dict, velocity_data: Dict, bottlenecks: Dict) -> List[str]:
        """Generate actionable insights from analytics data"""
        insights = []

        # Completion rate insights
        if completion_data.get('overall_rate', 0) > 80:
            insights.append("🎯 Excellent completion rate! You're highly productive.")
        elif completion_data.get('overall_rate', 0) > 60:
            insights.append("📈 Good completion rate. Consider optimizing workflow for even better results.")
        else:
            insights.append("⚠️ Low completion rate. Focus on task prioritization and time management.")

        # Velocity insights
        trend = velocity_data.get('trend', 'stable')
        if trend == 'increasing':
            insights.append("🚀 Task completion velocity is increasing! Maintain this momentum.")
        elif trend == 'decreasing':
            insights.append("📉 Velocity is declining. Consider reducing task complexity or improving focus time.")

        # Bottleneck insights
        long_running = len(bottlenecks.get('long_running_tasks', []))
        if long_running > 0:
            insights.append(f"🔍 {long_running} tasks have been open for >7 days. Consider breaking them into smaller chunks.")

        high_priority_open = len(bottlenecks.get('high_priority_open', []))
        if high_priority_open > 0:
            insights.append(f"🎯 {high_priority_open} high-priority tasks are still open. Focus on these first.")

        return insights

    def generate_performance_report(self, issues: List[Dict]) -> Dict:
        """Generate comprehensive performance report"""
        completion_data = self.analyze_task_completion(issues)
        velocity_data = self.analyze_velocity_trends(issues)
        bottlenecks = self.identify_bottlenecks(issues)
        insights = self.generate_insights(completion_data, velocity_data, bottlenecks)

        return {
            'report_date': datetime.now().isoformat(),
            'completion_analysis': completion_data,
            'velocity_analysis': velocity_data,
            'bottleneck_analysis': bottlenecks,
            'insights': insights,
            'summary': {
                'total_issues': len(issues),
                'open_issues': len([i for i in issues if i['state'] == 'open']),
                'closed_issues': len([i for i in issues if i['state'] == 'closed']),
                'overall_health': 'excellent' if completion_data.get('overall_rate', 0) > 80 else 
                               'good' if completion_data.get('overall_rate', 0) > 60 else 'needs_improvement'
            }
        }

# Example usage
if __name__ == "__main__":
    analytics = ProductivityAnalytics()
    print("📊 Seven Productivity Analytics Engine ready")


