\"\"\"
GitHub Integration for Seven Productivity System
Handles GitHub API interactions and repository management
\"\"\"

import requests
import json
from typing import Dict, List, Optional

class GitHubIntegration:
    def __init__(self, token: Optional[str] = None):
        self.token = token
        self.base_url = \"https://api.github.com\"
        self.repo_owner = \"Arnarsson\"
        self.repo_name = \"Seven\"

    def create_issue(self, title: str, body: str, labels: List[str] = None) -> Dict:
        \"\"\"Create a new GitHub issue\"\"\"
        url = f\"{self.base_url}/repos/{self.repo_owner}/{self.repo_name}/issues\"

        data = {
            \"title\": title,
            \"body\": body
        }

        if labels:
            data[\"labels\"] = labels

        headers = {\"Accept\": \"application/vnd.github.v3+json\"}
        if self.token:
            headers[\"Authorization\"] = f\"token {self.token}\"

        response = requests.post(url, json=data, headers=headers)
        return response.json()

    def list_issues(self, state: str = \"open\", labels: str = None) -> List[Dict]:
        \"\"\"List repository issues\"\"\"
        url = f\"{self.base_url}/repos/{self.repo_owner}/{self.repo_name}/issues\"

        params = {\"state\": state}
        if labels:
            params[\"labels\"] = labels

        headers = {\"Accept\": \"application/vnd.github.v3+json\"}
        if self.token:
            headers[\"Authorization\"] = f\"token {self.token}\"

        response = requests.get(url, params=params, headers=headers)
        return response.json()

    def update_issue(self, issue_number: int, title: str = None, body: str = None, 
                    state: str = None, labels: List[str] = None) -> Dict:
        \"\"\"Update an existing issue\"\"\"
        url = f\"{self.base_url}/repos/{self.repo_owner}/{self.repo_name}/issues/{issue_number}\"

        data = {}
        if title:
            data[\"title\"] = title
        if body:
            data[\"body\"] = body
        if state:
            data[\"state\"] = state
        if labels:
            data[\"labels\"] = labels

        headers = {\"Accept\": \"application/vnd.github.v3+json\"}
        if self.token:
            headers[\"Authorization\"] = f\"token {self.token}\"

        response = requests.patch(url, json=data, headers=headers)
        return response.json()

    def add_comment(self, issue_number: int, comment: str) -> Dict:
        \"\"\"Add comment to an issue\"\"\"
        url = f\"{self.base_url}/repos/{self.repo_owner}/{self.repo_name}/issues/{issue_number}/comments\"

        data = {\"body\": comment}

        headers = {\"Accept\": \"application/vnd.github.v3+json\"}
        if self.token:
            headers[\"Authorization\"] = f\"token {self.token}\"

        response = requests.post(url, json=data, headers=headers)
        return response.json()

    def get_repository_stats(self) -> Dict:
        \"\"\"Get repository statistics\"\"\"
        # Get repository info
        repo_url = f\"{self.base_url}/repos/{self.repo_owner}/{self.repo_name}\"
        headers = {\"Accept\": \"application/vnd.github.v3+json\"}
        if self.token:
            headers[\"Authorization\"] = f\"token {self.token}\"

        repo_response = requests.get(repo_url, headers=headers)
        repo_data = repo_response.json()

        # Get issues data
        issues = self.list_issues(state=\"all\")

        stats = {
            \"repository\": {
                \"name\": repo_data.get(\"name\"),
                \"stars\": repo_data.get(\"stargazers_count\", 0),
                \"forks\": repo_data.get(\"forks_count\", 0),
                \"open_issues\": repo_data.get(\"open_issues_count\", 0)
            },
            \"productivity\": {
                \"total_issues\": len(issues),
                \"open_issues\": len([i for i in issues if i[\"state\"] == \"open\"]),
                \"closed_issues\": len([i for i in issues if i[\"state\"] == \"closed\"])
            }
        }

        return stats

# Example usage
if __name__ == \"__main__\":
    github = GitHubIntegration()
    print(\"🔗 GitHub Integration ready\")

    # Test basic functionality
    stats = github.get_repository_stats()
    print(f\"Repository stats: {stats}\")

