function() {
  // Function to get cookie value
  function getCookie(name) {
    var nameEQ = name + "=";
    var ca = document.cookie.split(';');
    for(var i=0; i < ca.length; i++) {
      var c = ca[i];
      while (c.charAt(0) == ' ') c = c.substring(1, c.length);
      if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length, c.length);
    }
    return null;
  }

  // Function to parse UTM parameters from URL
  function getUTMParams() {
    var utmParams = {};
    var query = window.location.search.substring(1);
    var vars = query.split('&');
    for (var i = 0; i < vars.length; i++) {
      var pair = vars[i].split('=');
      if (pair[0].indexOf('utm_') === 0) {
        utmParams[pair[0]] = decodeURIComponent(pair[1]);
      }
    }
    return utmParams;
  }

  var utmParams = getUTMParams();

  // If UTM parameters are in the URL, save them to a cookie
  if (Object.keys(utmParams).length > 0) {
    document.cookie = "utm_params=" + JSON.stringify(utmParams) + "; path=/; max-age=2592000"; // 30 days expiry
  } else {
    // If not in URL, try to get from cookie
    var cookieValue = getCookie('utm_params');
    if (cookieValue) {
      try {
        utmParams = JSON.parse(cookieValue);
      } catch (e) {
        console.error('Failed to parse utm_params cookie:', e);
      }
    }
  }

  return utmParams;
}

function() {
  function getUTMParams() {
