const generateDailyData = (campaigns, days) => {
  return Array.from({ length: days }, (_, day) => {
    const dailyData = campaigns.map(campaign => {
      const progress = (day + 1) / days;
      return {
        name: campaign.Campaign,
        Impressions: Math.round(campaign.Impressions * progress),
        Clicks: Math.round(campaign.Clicks * progress),
        Conversions: Math.round(campaign.Conversions * progress),
        Cost: Math.round(campaign.Cost * progress)
      };
    });
    dailyData.sort((a, b) => b.Clicks - a.Clicks);
    return { day: day + 1, campaigns: dailyData.slice(0, 10) };
  });
};

