const AnimatedCampaignPerformance = () => {
  const [currentDay, setCurrentDay] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [metric, setMetric] = useState('Clicks');
  const dailyData = generateDailyData(campaigns, 30);

  useEffect(() => {
    let interval;
    if (isPlaying) {
      interval = setInterval(() => {
        setCurrentDay((prevDay) => (prevDay + 1) % 30);
      }, 200);
    }
    return () => clearInterval(interval);
  }, [isPlaying]);

  const handlePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  const handleReset = () => {
    setCurrentDay(0);
    setIsPlaying(false);
  };

  const handleMetricChange = (event) => {
    setMetric(event.target.value);
  };

  return (
    <div className="p-4 bg-white rounded-lg shadow">
      <h2 className="text-xl font-bold mb-4">Campaign Performance Over Time</h2>
      <div className="mb-4 flex items-center">
        <button 
          onClick={handlePlayPause} 
          className="mr-2 px-4 py-2 bg-blue-500 text-white rounded"
        >
          {isPlaying ? 'Pause' : 'Play'}
        </button>
        <button 
          onClick={handleReset}
          className="mr-4 px-4 py-2 bg-gray-500 text-white rounded"
        >
          Reset
        </button>
        <select 
          value={metric} 
          onChange={handleMetricChange}
          className="px-2 py-1 border rounded"
        >
          <option value="Clicks">Clicks</option>
          <option value="Impressions">Impressions</option>
          <option value="Conversions">Conversions</option>
          <option value="Cost">Cost (DKK)</option>
        </select>
      </div>
      <div className="text-lg font-semibold mb-2">Day {currentDay + 1}</div>
      <ResponsiveContainer width="100%" height={500}>
        <BarChart
          data={dailyData[currentDay].campaigns}
          layout="vertical"
          margin={{ top: 5, right: 30, left: 200, bottom: 5 }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis type="number" />
          <YAxis type="category" dataKey="name" width={180} />
          <Tooltip />
          <Legend />
          <Bar dataKey={metric} fill="#8884d8" />
